import React, { useState } from "react";
import axios from "axios";

function UploadForm() {
    const [file, setFile] = useState(null);
    const [position, setPosition] = useState("");
    const [company, setCompany] = useState("");
    const [jobDescription, setJobDescription] = useState("");
    const [parsedData, setParsedData] = useState(null);
    const [questions, setQuestions] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file || !position || !company) {
            alert("Please fill out all fields and upload a file.");
            return;
        }

        const formData = new FormData();
        formData.append("file", file);
        formData.append("position", position);
        formData.append("company", company);
        formData.append("job_description", jobDescription);

        try {
            const res = await axios.post("http://127.0.0.1:5000/upload-resume", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });
            setParsedData(JSON.parse(res.data.parsed_data)); // Parsed resume data
            setQuestions(res.data.interview_questions); // Generated interview questions
        } catch (err) {
            console.error(err);
            alert("Failed to upload file. Please try again.");
        }
    };

    return (
        <div>
            <h1>AI Resume Parser and Interview Question Generator</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Job Position:
                    <input
                        type="text"
                        value={position}
                        onChange={(e) => setPosition(e.target.value)}
                        placeholder="Enter the job position you're applying for"
                        required
                    />
                </label>
                <br />
                <label>
                    Company Name:
                    <input
                        type="text"
                        value={company}
                        onChange={(e) => setCompany(e.target.value)}
                        placeholder="Enter the company name"
                        required
                    />
                </label>
                <br />
                <label>
                    Job Description:
                    <textarea
                        value={jobDescription}
                        onChange={(e) => setJobDescription(e.target.value)}
                        placeholder="Optional: Add a detailed job description"
                    />
                </label>
                <br />
                <label>
                    Upload Resume:
                    <input
                        type="file"
                        accept=".pdf, .docx"
                        onChange={(e) => setFile(e.target.files[0])}
                        required
                    />
                </label>
                <br />
                <button type="submit">Upload and Generate</button>
            </form>

            {/* Display parsed resume data */}
            {parsedData && (
                <div>
                    <h2>Parsed Resume Data</h2>
                    <pre>{JSON.stringify(parsedData, null, 2)}</pre>
                </div>
            )}

            {/* Display generated interview questions */}
            {questions && (
                <div>
                    <h2>Generated Interview Questions</h2>
                    <ol>
                        {questions.map((question, index) => (
                            <li key={index}>{question}</li>
                        ))}
                    </ol>
                </div>
            )}
        </div>
    );
}

export default UploadForm;
