import React, { useState } from "react";

function UploadForm() {
    const [file, setFile] = useState(null);
    const [position, setPosition] = useState("");
    const [company, setCompany] = useState("");
    const [jobDescription, setJobDescription] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Form submitted");
        // Handle form submission logic here
    };

    return (
        <div className="container mt-5">
            <div className="card shadow p-4">
                <h1 className="text-center mb-4 text-primary">AI Interview Questions Generator</h1>
                <p className="text-center mb-4">Generate tailored interview questions based on your resume and job details!</p>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="position" className="form-label">
                            Job Position
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            id="position"
                            placeholder="Enter the job position"
                            value={position}
                            onChange={(e) => setPosition(e.target.value)}
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="company" className="form-label">
                            Company Name
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            id="company"
                            placeholder="Enter the company name"
                            value={company}
                            onChange={(e) => setCompany(e.target.value)}
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="jobDescription" className="form-label">
                            Job Description
                        </label>
                        <textarea
                            className="form-control"
                            id="jobDescription"
                            rows="3"
                            placeholder="Optional: Add a detailed job description"
                            value={jobDescription}
                            onChange={(e) => setJobDescription(e.target.value)}
                        ></textarea>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="resume" className="form-label">
                            Upload Resume
                        </label>
                        <input
                            type="file"
                            className="form-control"
                            id="resume"
                            onChange={(e) => setFile(e.target.files[0])}
                        />
                    </div>
                    <button type="submit" className="btn btn-primary w-100">
                        Upload and Generate
                    </button>
                </form>
            </div>
        </div>
    );
}

export default UploadForm;
