import React, { useState } from "react";
import UploadForm from "./components/UploadForm";
import QuestionsDisplay from "./components/QuestionsDisplay";

function App() {
    const [questions, setQuestions] = useState([]); // State to hold questions

    return (
        <div>
            <header className="hero">
                <h1>AI Interview Questions Generator</h1>
                <p>Generate tailored interview questions based on your resume and job details!</p>
            </header>
            <main className="container">
                {/* Pass the callback to UploadForm */}
                <UploadForm onQuestionsGenerated={(newQuestions) => setQuestions(newQuestions)} />
                {/* Pass the questions to QuestionsDisplay */}
                <QuestionsDisplay questions={questions} />
            </main>
            <footer className="text-center mt-5">
                <p>
                    &copy; 2024 AI Interview Questions Generator. Built with ❤️ by{" "}
                    <a href="#">Lorenzo DeMarni</a>.
                </p>
            </footer>
        </div>
    );
}

export default App;
