import React, { useState } from "react";
import axios from "axios";

const UploadForm = () => {
  const [file, setFile] = useState(null);
  const [position, setPosition] = useState("");
  const [company, setCompany] = useState("");
  const [jobDescription, setJobDescription] = useState("");

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handlePositionChange = (event) => {
    setPosition(event.target.value);
  };

  const handleCompanyChange = (event) => {
    setCompany(event.target.value);
  };

  const handleJobDescriptionChange = (event) => {
    setJobDescription(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!file || !position || !company) {
      alert("Please fill in all required fields (Resume, Position, and Company).");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("position", position);
    formData.append("company", company);
    formData.append("job_description", jobDescription);

    try {
      const response = await axios.post("http://127.0.0.1:5000/upload-resume", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      console.log("Response:", response.data);
      alert("Interview questions generated successfully. Check the console for details.");
    } catch (error) {
      console.error("Error uploading data:", error);
      alert("Failed to upload data. Please try again.");
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "0 auto" }}>
      <h1>AI Job Interview Question Generator</h1>
      <form onSubmit={handleSubmit}>
        <label>
          <strong>Upload Resume (PDF or DOCX):</strong>
          <input type="file" onChange={handleFileChange} required />
        </label>
        <br />
        <label>
          <strong>Position Applying For:</strong>
          <input type="text" value={position} onChange={handlePositionChange} required />
        </label>
        <br />
        <label>
          <strong>Company Name:</strong>
          <input type="text" value={company} onChange={handleCompanyChange} required />
        </label>
        <br />
        <label>
          <strong>Job Description (Optional):</strong>
          <textarea
            value={jobDescription}
            onChange={handleJobDescriptionChange}
            rows="6"
            style={{ width: "100%" }}
            placeholder="Paste the job description here for better question generation."
          ></textarea>
        </label>
        <br />
        <button type="submit">Generate Interview Questions</button>
      </form>
    </div>
  );
};

export default UploadForm;
